const fs = require('fs-extra');
export const getFileContent = (filePath) => {
    try {
        let fileContent = fs.readFileSync(filePath, 'utf8');
        return fileContent;
    }
    catch (e) {
        return '';
    }
};
export const saveJson = (filePath, json) => {
    fs.writeJsonSync(filePath, json);
};
